-- Criacao da Base de dados
CREATE DATABASE IF NOT EXISTS `dbloja` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;
USE `dbloja`;

-- Criacao da Tabelas

CREATE TABLE `Categoria` (
  `CatID` INTEGER NOT NULL AUTO_INCREMENT,
  `CatNome` VARCHAR(40),
  `CatStatus` TINYINT(1) DEFAULT 0,
  PRIMARY KEY (`CatID`)
);

CREATE TABLE `SubCategoria` (
  `SubCatID` INTEGER NOT NULL AUTO_INCREMENT,
  `SubCatNome` VARCHAR(40),
  `SubCatStatus` TINYINT(1) DEFAULT 0,
  `CatID` INTEGER,
  PRIMARY KEY (`SubCatID`),
  FOREIGN KEY (`CatID`) REFERENCES categoria (`CatID`)
);

CREATE TABLE `Marca` (
  `MarcaID` INTEGER NOT NULL AUTO_INCREMENT,
  `MarcaNome` VARCHAR(40),
  `MarcaSite` VARCHAR(60),
  `MarcaStatus` TINYINT(1) DEFAULT 0,
  PRIMARY KEY (`MarcaID`)
);

CREATE TABLE `Produto` (
  `ProdID` INTEGER NOT NULL AUTO_INCREMENT,
  `ProdNome` varchar(120) DEFAULT NULL,
  `ProdPrecoNormal` decimal(15,2) DEFAULT '0.00',
  `ProdPrecoVenda` decimal(15,2) DEFAULT '0.00',
  `ProdDetalhe` longtext,
  `ProdDisplayDestaque` tinyint(1) DEFAULT '0',
  `ProdDisplayOferta` tinyint(1) DEFAULT '0',
  `ProdDisplayHome` tinyint(1) DEFAULT '0',
  `ProdDisplayStatus` tinyint(1) DEFAULT '0',
  `ProdDisplayLancamento` tinyint(1) DEFAULT '0',
  `SubCatID` INTEGER DEFAULT NULL,
  `MarcaID` INTEGER DEFAULT NULL,
  `ProdPeso` varchar(6) DEFAULT NULL,
  `ProdFreteGratis` tinyint(1) DEFAULT '0',
  PRIMARY KEY (`ProdID`),
  KEY `SubCatID` (`SubCatID`),
  KEY `MarcaID` (`MarcaID`),
  CONSTRAINT `produto_ibfk_1` FOREIGN KEY (`SubCatID`) REFERENCES `subcategoria` (`SubCatID`),
  CONSTRAINT `produto_ibfk_2` FOREIGN KEY (`MarcaID`) REFERENCES `marca` (`MarcaID`)
);

CREATE TABLE `ProdutoFoto` (
  `ProdFotoID` INTEGER NOT NULL AUTO_INCREMENT,
  `ProdID` INTEGER,
  `ProdFotoArquivo` VARCHAR(15),
  PRIMARY KEY (`ProdFotoID`),
  FOREIGN KEY (`ProdID`) REFERENCES produto (`ProdID`)
);

CREATE TABLE `NavegacaoCategoria` (
  `NavCatID` INTEGER NOT NULL AUTO_INCREMENT,
  `NavCatKey` VARCHAR(10),
  `CatID` INTEGER,
  PRIMARY KEY (`NavCatID`),
  FOREIGN KEY (`CatID`) REFERENCES Categoria(`CatID`)
);

CREATE TABLE `NavegacaoProduto` (
  `NavProdID` INTEGER NOT NULL AUTO_INCREMENT,
  `NavProdKey` VARCHAR(10),
  `ProdID` INTEGER,
  PRIMARY KEY (`NavProdID`),
  FOREIGN KEY (`ProdID`) REFERENCES produto (`ProdID`)
);

CREATE TABLE `Cliente` (
  `CliID` INTEGER NOT NULL AUTO_INCREMENT,
  `CliNome` VARCHAR(40),
  `CliEndereco` VARCHAR(40),
  `CliCep` VARCHAR(10),
  `CliBairro` VARCHAR(40),
  `CliCidade` VARCHAR(40),
  `CliEstado` VARCHAR(2),
  `CliComp` VARCHAR(30),
  `CliDataCad` DATETIME,
  `CliSenha` VARCHAR(20),
  `CliEmail` VARCHAR(45),
  `CliTelefone` VARCHAR(15),
  `CliCelular` VARCHAR(15),
  `CliDataNasc` DATETIME,
  `CliSexo` TINYINT(1) DEFAULT 0,
  PRIMARY KEY (`CliID`)
);

CREATE TABLE `Pedido` (
  `PedID` INTEGER NOT NULL AUTO_INCREMENT,
  `PedData` DATETIME,
  `CliID` INTEGER,
  `PedFreteValor` DECIMAL(10,2) DEFAULT 0,
  `PedFormaPag` VARCHAR(30),
  `PedCEP` VARCHAR(10),
  `PedCliNome` VARCHAR(40),
  `PedEndereco` VARCHAR(40),
  `PedEndComplemento` VARCHAR(30),
  `PedCidade` VARCHAR(40),
  `PedBairro` VARCHAR(40),
  `PedEstado` VARCHAR(2),
  `PedProcessado` TINYINT(1) DEFAULT 0,
  `PedTransacoes` VARCHAR(255),
  `PedPresente` TINYINT(1) DEFAULT 0,
  `PedFreteObs` LONGTEXT,
  `PedFreteNome` VARCHAR(15),
  `PedStatus` TINYINT(1) DEFAULT 0,
  PRIMARY KEY (`PedID`),
  FOREIGN KEY (`CliID`) REFERENCES cliente (`CliID`)
);

CREATE TABLE `PedidoItem` (
  `PedItemID` INTEGER NOT NULL AUTO_INCREMENT,
  `PedID` INTEGER,
  `ProdID` INTEGER,
  `PedItemQuant` INTEGER NOT NULL,
  `PedPreco` DECIMAL(10,2) NOT NULL DEFAULT 0,
  PRIMARY KEY (`PedItemID`),
  FOREIGN KEY (`PedID`) REFERENCES pedido (`PedID`),
  FOREIGN KEY (`ProdID`) REFERENCES produto (`ProdID`)
);

CREATE TABLE `Usuario` (
  `UsuarioID` INTEGER NOT NULL AUTO_INCREMENT,
  `UsuarioNome` VARCHAR(30) NOT NULL,
  `UsuarioEmail` VARCHAR(50) NOT NULL,
  `UsuarioSenha` VARCHAR(30) NOT NULL,
  `UsuarioStatus` TINYINT(1) DEFAULT 0,
  PRIMARY KEY (`UsuarioID`)
);

